import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import useMenuStore from "../store/useMenuStore.js";
import html2canvas from "html2canvas";
import Background from "../assets/Background.png";
import Highlight from "../assets/Highlight.png";
import Home from "../assets/Home.svg";
import Export from "../assets/Export.svg";

function Result() {
  const navigate = useNavigate();

  const { /*results,*/ resetAll } = useMenuStore();

  // 테스트용
  const results = [
    {
      meal: "아침",
      restaurant_name: "샐러디",
      name: "닭가슴살 샐러드",
      description: "신선한 채소와 닭가슴살이 들어간 건강식",
      calories: 350,
      image_url: "data:image/jpeg;base64,",
      url: "...",
      latitude: 37.585761195,
      longitude: 127.030573281
    },
    {
      meal: "점심",
      restaurant_name: "본죽",
      name: "소고기 죽",
      description: "맛있는 소고기 죽",
      calories: 350,
      image_url: "data:image/jpeg;base64,",
      url: "...",
      latitude: 37.586133050,
      longitude: 127.029229203
    },
    {
      meal: "저녁",
      restaurant_name: "샐러디",
      name: "닭가슴살 샐러드",
      description: "신선한 채소와 닭가슴살이 들어간 건강식",
      calories: 350,
      image_url: "data:image/jpeg;base64,",
      url: "...",
      latitude: 37.585761195,
      longitude: 127.030573281
    }
  ];

  /*
  useEffect(() => {
    if (!results || results.length === 0) {
      console.warn("허가되지 않은 접근 감지, 메인으로 리다이렉트");
      navigate("/");
    }
  }, [results, navigate]);
  */

  const handleClickHome = () => {
    resetAll();
    navigate("/");
  }

  const handleExport = () => {
    const element = document.getElementById("capture-area");

    html2canvas(element, {
      useCORS: true, // 외부 이미지 저장
      scrollX: -window.scrollX,
      scrollY: -window.scrollY,
      ignoreElements: (el) => el.id === "floating-button",
    }).then((canvas) => {
      // 이미지 데이터 생성
      const link = document.createElement("a");
      link.download = "menumate.png";
      link.href = canvas.toDataURL("image/png");
      link.click();
    })
  }

  /* 지도 API */
  const apiKey = import.meta.env.VITE_GOOGLE_MAPS_API_KEY;
  /* URL 생성 */
  const getMapUrl = (lat, lng) => {
    if (!apiKey) {
      console.warn("⚠️ Google Maps API key is missing");
      return "";
    }

    return `https://maps.googleapis.com/maps/api/staticmap?center=${lat},${lng}&zoom=15&size=135x135&markers=color:red%7C${lat},${lng}&key=${apiKey}`;
  }

  return(
      <div id="capture-area" className="flex relative h-full justify-center bg-primary bg-cover bg-center"
           style={{backgroundImage: `url(${Background})`}}
      >
        <button id="floating-button" className="absolute flex justify-center items-center left-[31px] top-[46px] w-[58px] h-[58px]
                          bg-primary rounded-full shadow-[2px_2px_4px_rgba(0,0,0,0.25)]
                          hover:bg-accent
                          active:scale-95
                          transition-transform duration-150"
                onClick={handleClickHome}>
          <img
              src={Home}
              className="w-7 h-7"
          />
        </button>
        <button id="floating-button" className="absolute flex justify-center items-center right-[31px] bottom-[46px] w-[58px] h-[58px]
                          bg-primary rounded-full shadow-[2px_2px_4px_rgba(0,0,0,0.25)]
                          hover:bg-accent
                          active:scale-95
                          transition-transform duration-150"
                onClick={handleExport}>
          <img
              src={Export}
              className="w-7 h-7"
          />
        </button>
        <div className="flex flex-col mt-[155px] gap-6">
          {results.map((res, i) => (
              <div
                  key={i}
                  className={`flex min-h-[150px] items-center justify-end gap-[30px] ${
                      i % 2 === 0 ? "flex-row" : "flex-row-reverse"
                  }`}
              >
                {/* 메뉴 사진, 지도 */}
                <div className="relative flex items-center">
                  <div
                      className="absolute left-[74px] top-[84px] w-20 h-20 bg-gray-500 rounded-full bg-cover bg-center"
                      style={{backgroundImage: `url(${res.image_url})`}}/>
                  <div className="w-[135px] h-[135px] bg-gray-300">
                    <img
                        src={getMapUrl(res.latitude, res.longitude)}
                        alt="지도"
                        className="w-full h-full object-cover"
                    />
                  </div>
                </div>

                <div
                    className={`flex flex-col w-[145px] font-pen text-[22px] text-black ${
                        i % 2 === 1 && "text-right"
                    }`}>
                  <div className="text-2xl -mb-2">{res.meal}</div>
                  <div className="flex mb-1">
                    <img src={Highlight}
                         className={`w-10 ${ i % 2 === 0 ? "-ml-1.5" : "ml-[110px]" }`}
                    />
                  </div>
                  <div className="leading-tight">
                    <div>{res.restaurant_name}</div>
                    <div>{res.name}</div>
                    <div
                        className="text-gray-500 text-[20px]">{res.description}</div>
                  </div>
                </div>
              </div>
          ))}
        </div>
      </div>
  );
}

export default Result;